import React from 'react'

const ImageDetection = () => {
    const dropZone = document.getElementById('dropZone');
    const fileInput = document.getElementById('fileInput');
    const previewImage = document.getElementById('previewImage');
    const detectButton = document.querySelector('.detect-button');
    const resultText = document.getElementById('resultText');

    // Prevent default drag behaviors
    ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
        dropZone.addEventListener(eventName, preventDefaults, false);
        document.body.addEventListener(eventName, preventDefaults, false);
    });

    // Highlight drop zone when item is dragged over it
    ['dragenter', 'dragover'].forEach(eventName => {
        dropZone.addEventListener(eventName, highlight, false);
    });

    ['dragleave', 'drop'].forEach(eventName => {
        dropZone.addEventListener(eventName, unhighlight, false);
    });

    // Handle dropped files
    dropZone.addEventListener('drop', handleDrop, false);

    // Handle file input change
    fileInput.addEventListener('change', handleFiles, false);

    // Detect button click
    detectButton.addEventListener('click', function() {
        if (previewImage.src) {
            resultText.textContent = 'Processing image... (Simulated result)';
            setTimeout(() => {
                resultText.textContent = 'Object Detection: Car, Person, Tree';
            }, 1500);
        } else {
            resultText.textContent = 'Please upload an image first';
        }
    });

    function preventDefaults(e) {
        e.preventDefault();
        e.stopPropagation();
    }

    function highlight() {
        dropZone.classList.add('drag-over');
    }

    function unhighlight() {
        dropZone.classList.remove('drag-over');
    }

    function handleDrop(e) {
        const dt = e.dataTransfer;
        const files = dt.files;
        handleFileUpload(files[0]);
    }

    function handleFiles() {
        handleFileUpload(fileInput.files[0]);
    }

    function handleFileUpload(file) {
        if (file && file.type.startsWith('image/')) {
            const reader = new FileReader();
            reader.onload = function(e) {
                previewImage.src = e.target.result;
                previewImage.style.display = 'block';
            }
            reader.readAsDataURL(file);
        } else {
            alert('Please upload a valid image file');
        }
    }
  return (
        <div id="main-container">
                <div className="container">
        <div id="dropZone" className="flex-box">
            <div className="upload-container">
            <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="upload-icon">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                </svg> 
                <p className="drag-text">Drag and Drop an image or</p>
                <input type="file" id="fileInput" accept="image/*"/>
                <label for="fileInput" className="upload-label">
                    Choose Image
                </label>
                <div style={{display:'flex' , flexDirection}}>
                <img id="previewImage" className="preview-image" src={''} alt="ImagePreview" style={{display:'none'}}/>
                <div id="fileName" style="display: none;"></div>
                </div>
            </div>
        </div>

        <button className="detect-button">Detect</button>

        <div className="flex-box">
            <div className="result-container">
                <p id="resultText">Results will appear here</p>
            </div>
        </div>
    </div>
            </div>
  )
}

export default ImageDetection
