import React, { useRef, useState, useEffect } from 'react';
import './ImageDetection.css'; // Assuming you'll create a separate CSS file

function ImageDetection() {
    const [previewImage, setPreviewImage] = useState(null);
    const [fileName, setFileName] = useState('');
    const [resultImage, setResultImage] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');
    const dropZoneRef = useRef(null);
    const fileInputRef = useRef(null);

    const preventDefaults = (e) => {
        e.preventDefault();
        e.stopPropagation();
    };

    const highlight = () => {
        if (dropZoneRef.current) {
            dropZoneRef.current.classList.add('drag-over');
        }
    };

    const unhighlight = () => {
        if (dropZoneRef.current) {
            dropZoneRef.current.classList.remove('drag-over');
        }
    };

    const handleFileUpload = (file) => {
        if (file && file.type.startsWith('image/')) {
            const reader = new FileReader();
            reader.onload = (e) => {
                setPreviewImage(e.target.result);
                setFileName(file.name);
                // Reset previous states
                setResultImage(null);
                setError('');
            };
            reader.readAsDataURL(file);
        } else {
            setError('Please upload a valid image file');
        }
    };

    const handleDrop = (e) => {
        preventDefaults(e);
        unhighlight();
        const dt = e.dataTransfer;
        const files = dt.files;
        handleFileUpload(files[0]);
    };

    const handleFileInputChange = (e) => {
        handleFileUpload(e.target.files[0]);
    };

    const handleDetect = async () => {
        if (!previewImage) {
            setError('Please upload an image first');
            return;
        }

        setIsLoading(true);
        setError('');
        setResultImage(null);

        try {
            // Convert base64 to Blob for upload
            const response = await fetch(previewImage);
            const blob = await response.blob();

            const formData = new FormData();
            formData.append('file', blob, fileName);

            const detectResponse = await fetch('http://127.0.0.1:3001/detect', {
                method: 'POST',
                body: formData,
            });

            if (!detectResponse.ok) {
                throw new Error('Detection failed');
            }

            const data = await detectResponse.json();

            if (data.image && data.image.length > 0) {
                setResultImage(data.image[0]);
            } else {
                setError('No results found');
            }
        } catch (err) {
            console.error('Detection error:', err);
            setError('Failed to detect image. Please try again.');
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        const dropZone = dropZoneRef.current;

        if (dropZone) {
            ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
                dropZone.addEventListener(eventName, preventDefaults);
                document.body.addEventListener(eventName, preventDefaults);
            });

            ['dragenter', 'dragover'].forEach(eventName => {
                dropZone.addEventListener(eventName, highlight);
            });

            ['dragleave', 'drop'].forEach(eventName => {
                dropZone.addEventListener(eventName, unhighlight);
            });

            dropZone.addEventListener('drop', handleDrop);
        }

        return () => {
            if (dropZone) {
                ['dragenter', 'dragover', 'dragleave', 'drop'].forEach(eventName => {
                    dropZone.removeEventListener(eventName, preventDefaults);
                    document.body.removeEventListener(eventName, preventDefaults);
                });
            }
        };
    }, []);

    return (
        <div className="image-detection-container">
            <div className="upload-section">
                <div 
                    id="dropZone" 
                    ref={dropZoneRef} 
                    className="drop-zone"
                >
                    <input 
                        type="file" 
                        id="fileInput" 
                        ref={fileInputRef}
                        accept="image/*" 
                        onChange={handleFileInputChange}
                        className="file-input"
                    />
                    <label htmlFor="fileInput" className="drop-zone-label">
                        <svg 
                            xmlns="http://www.w3.org/2000/svg" 
                            fill="none" 
                            viewBox="0 0 24 24" 
                            stroke="currentColor" 
                            className="upload-icon"
                        >
                            <path 
                                strokeLinecap="round" 
                                strokeLinejoin="round" 
                                strokeWidth="2" 
                                d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" 
                            />
                        </svg>
                        <p className="drag-text">
                            Drag and Drop an image or 
                            <span className="upload-link"> Choose Image</span>
                        </p>
                    </label>

                    {/* Preview Section */}
                    {previewImage && (
                        <div className="preview-container">
                            <img 
                                src={previewImage} 
                                alt="Preview" 
                                className="preview-image"
                            />
                            <div className="file-name">{fileName}</div>
                        </div>
                    )}
                </div>

                {/* Error Display */}
                {error && (
                    <div className="error-message">{error}</div>
                )}

                {/* Detect Button */}
                <button 
                    className="detect-button" 
                    onClick={handleDetect}
                    disabled={!previewImage || isLoading}
                >
                    {isLoading ? 'Detecting...' : 'Detect'}
                </button>
            </div>

            {/* Result Section */}
            <div className="result-section">
                {isLoading && (
                    <div className="loading-spinner">
                        Processing...
                    </div>
                )}

                {resultImage && (
                    <div className="result-image-container">
                        <h3>Detection Result</h3>
                        <img 
                            src={resultImage} 
                            alt="Detected Result" 
                            className="result-image"
                        />
                    </div>
                )}
            </div>
        </div>
    );
}

export default ImageDetection;
