import React, { useState, useEffect } from 'react';

const WebcamCapture = () => {
    const [isStreaming, setIsStreaming] = useState(true);

    useEffect(() => {
        // Start streaming when the component mounts
        setIsStreaming(true);

        return () => {
            // Stop streaming when the component unmounts
            setIsStreaming(false);
        };
    }, []);

    return (
        <div>
            <h1>Webcam Object Detection Stream</h1>

            {isStreaming ? (
                <img 
                    src="http://localhost:3001/webcam" 
                    alt="Webcam Stream"
                    style={{ width: '640px', height: '480px' }}
                />
            ) : (
                <p>Starting webcam stream...</p>
            )}
        </div>
    );
};

export default WebcamCapture;
