import React from 'react'

const ImageDetection = () => {
    const form = document.getElementById('uploadForm');
    const resultImage = document.getElementById('resultImage');

    form.addEventListener('submit', async (event) => {
        event.preventDefault();
        const formData = new FormData(form);
        console.log(formData);
        const response  = await fetch('http://127.0.0.1:3001/detect', {
            method: 'POST',
            body: formData,
        });
        document.getElementById('result').style.display = 'block';
        const data = await response.json();
        console.log(data);
        const firstUrl = data.image[0];
        resultImage.setAttribute('src', firstUrl);  
    });
  return (
    <div className='flex flex-col justify-center item-center'>
<div className="z-10 w-full h-full flex bg-white bg-opacity-60">
    <div className="extraOutline p-3 bg-white w-full bg-whtie m-auto rounded-lg">
        <div className="file_upload p-2 relative border-4 border-dotted border-gray-300 rounded-lg flex m-auto" style={{width: '80%',height:'100%'}}>
            <svg className="text-indigo-500 w-20 mx-auto mb-0" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 26 26" stroke="currentColor"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M7 16a4 4 0 01-.88-7.903A5 5 0 1115.9 6L16 6a5 5 0 011 9.9M15 13l-3-3m0 0l-3 3m3-3v12" /></svg>
            <div className="input_field flex flex-col w-max mx-auto text-center justify-center item-center">
                <form id="uploadForm"> 
                    <input className="text-sm cursor-pointer w-36 hidden" type="file" name='Image' multiple />
                    <div className="text bg-indigo-600 text-white border border-gray-300 rounded font-semibold cursor-pointer p-2 px-4 hover:bg-indigo-500">Select</div>
                    <div className="title text-indigo-500 uppercase mt-2">or drop Image here</div>
                </form>
            </div>
        </div>
    </div>
</div>
<button className="text bg-indigo-600 text-white border border-gray-300 rounded font-semibold cursor-pointer p-2 px-5 hover:bg-indigo-500 m-auto" type='submit'>Detect</button>
<div id='result' style={{display:'none'}}>
    <p>Image</p>
    <img src={''} alt="ImageResult" id='resultImage'/>
</div>
</div>
  )
}

export default ImageDetection
